mapper.insert:
insert into ask(
ask_no
,email
,ask_title
,ask_category
,ask_content
,ask_date
,reply_yn
)values(
(SELECT NVL(MAX(ask_no), 0) + 1 FROM ask)
,#{email}
,#{ask_title}
,#{ask_category}
,#{ask_content}
,default
,default
)

mapper.select:
select *
from ask where email=#{email} 
order by ask_no desc
