mapper.insert:
INSERT
	INTO
	FONGDANG."OPTION"
		(OPTION_NO,
		P_NO,
		OPTION_NAME,
		OPTION_INFO,
		OPTION_PRICE,
		OPTION_LIMIT)
values(
		 #{option_no}
		,#{p_no}
		,#{option_name}
		,#{option_info}
		,#{option_price}
		,#{option_limit}
)

mapper.update:
update option 
   set option_name=#{option_name}
		,option_info=#{option_info}
		,option_price=#{option_price}
		,option_limit=#{option_limit}
where option_no=#{option_no}
